//*****************************************************************************
//
//! SPI Master mode main loop
//!
//! This function configures SPI modelue as master and enables the channel for
//! communication
//!
//! \return None.
//
//*****************************************************************************
void MasterMain()
{
    int N = 4096;
    complex v[4096], scratch[4096];
    long data[4100];
    long adc_value;
    int i;
    int max_phase = 0, best_index = 0 , j = 0,temp;
    float max_val,abs_val;
    float freq,freq_heart;
    //
    // Set Tx buffer index
    //
    ucTxBuffNdx = 0;
    ucRxBuffNdx = 0;

    //
    // Reset SPI
    //
    MAP_SPIReset(GSPI_BASE);

    //
    // Configure SPI interface
    //
    MAP_SPIConfigSetExpClk(GSPI_BASE,MAP_PRCMPeripheralClockGet(PRCM_GSPI),
                     SPI_IF_BIT_RATE,SPI_MODE_MASTER,SPI_SUB_MODE_0,
                     (SPI_SW_CTRL_CS |
                     SPI_4PIN_MODE |
                     SPI_TURBO_OFF |
                     SPI_CS_ACTIVELOW |
                     SPI_WL_8));

    //
    // Enable SPI for communication
    //

    MAP_SPIEnable(GSPI_BASE);
    // FILE *myfile;
    while(1)
    {
        printf("Press 's' and enter to start\n");
        char ipt[10] = "      ";
        while (ipt[0] != 's')
        {
            scanf("%s",&ipt);
        }
        printf("Starting the heart-beat monitoring process...\n\r");

        //myfile = fopen("C:\\Users\\Sutej Kulkarni\\Desktop\\UCDAVIS\\new1.csv", "w+");

        for(i=0;i<4096;i++)
        {
            // Downsampling, data[i] is being overwritten and we drop 5 values for
            // downsampling. One sample is obtained every 2891Hz, so droping 5 values
            // makes sampling rate to 482Hz.

            MAP_SPITransfer(GSPI_BASE,0,g_ucRxBuff,4,
                    SPI_CS_ENABLE|SPI_CS_DISABLE);

            adc_value = ((int)(g_ucRxBuff[0])<<8 | g_ucRxBuff[1] );
            data[i]=adc_value;
            MAP_SPITransfer(GSPI_BASE,0,g_ucRxBuff,4,
                    SPI_CS_ENABLE|SPI_CS_DISABLE);

            adc_value = ((int)(g_ucRxBuff[0])<<8 | g_ucRxBuff[1] );
            data[i]=adc_value;
            MAP_SPITransfer(GSPI_BASE,0,g_ucRxBuff,4,
                    SPI_CS_ENABLE|SPI_CS_DISABLE);

            adc_value = ((int)(g_ucRxBuff[0])<<8 | g_ucRxBuff[1] );
            data[i]=adc_value;
            MAP_SPITransfer(GSPI_BASE,0,g_ucRxBuff,4,
                    SPI_CS_ENABLE|SPI_CS_DISABLE);

            adc_value = ((int)(g_ucRxBuff[0])<<8 | g_ucRxBuff[1] );
            data[i]=adc_value;
            MAP_SPITransfer(GSPI_BASE,0,g_ucRxBuff,4,
                   SPI_CS_ENABLE|SPI_CS_DISABLE);

           adc_value = ((int)(g_ucRxBuff[0])<<8 | g_ucRxBuff[1] );
           data[i]=adc_value;
           MAP_SPITransfer(GSPI_BASE,0,g_ucRxBuff,4,
                              SPI_CS_ENABLE|SPI_CS_DISABLE);

           adc_value = ((int)(g_ucRxBuff[0])<<8 | g_ucRxBuff[1] );
           data[i]=adc_value;
        }

        printf("Done Reading\n\r");

        // Convert the data to 0 - 1024
        for(i=0;i<4096;i++)
        {
            data[i] >>=3;
            data[i] = data[i] & 0x3FF;
            v[i].Re = data[i];
            v[i].Im = 0;
            //fprintf(myfile,"%d\n",data[i]);
        }
        //fclose(myfile);

        // Perform the FFT to find the frequency of the carrier wave.
        fft( v, N, scratch );

        // Detect Carrier Frequency.
        abs_val = sqrt(pow(v[50].Re,2) + pow(v[50].Im,2));
        max_val=abs_val;
        int max_i=1;

        // Band pass filter, we go over bins from 50 to 2000.
        for(i=50;i<2000;i++)
        {
            // calculate the magnitude.
            abs_val = sqrt(pow(v[i].Re,2) + pow(v[i].Im,2));


            if(abs_val>max_val)
                {
                    max_val=abs_val;
                    max_i=i;
                    freq = i * 481.88/4096.00;
                }
        }

        //printf("Carrier Frequency is %f \n\r",freq);

        // Square wave is generated in MATLAB, from this.
        // It is already saved in array sq_wav, for 230Hz.
        // Phase shifting.

        // Phase shift using 20 points.
        for (j = 0; j < 20 ; j++)
        {
            temp = 0;

            // multiply and add the signal.
            for (i = 0; i < 20; i++)
            {
                temp += data[i] * sq_wav[j+i];
            }

            // if new sum is greater then save it in best_index.
            if (temp > max_phase)
            {
                max_phase = temp;
                best_index = j;
            }
        }

        printf("Shifting signal by %d points\n\r",best_index);
        printf("matching the phase!\n\r");

        // De-modulate the signal.
        for (i = 0; i < 4095-best_index; i++)
        {
            data[i]= data[i] * sq_wav[best_index+i];
        }
        printf("Done matching!\n\r");

        // Prepare the data for the FFT again.
        for(i=0;i<4096;i++)
        {
            v[i].Re = data[i];
            v[i].Im = 0;
        }

        // Perform the FFT to find the frequency of the Heart wave.
        fft( v, N, scratch );


        // Band pass filter, for frequency 0.77Hz to 2.2Hz
        max_val = 0;
        for(i=7;i<20;i++)
        {
            abs_val = sqrt(pow(v[i].Re,2) + pow(v[i].Im,2));

            if(abs_val>max_val)
            {
                max_val=abs_val;
                max_i=i;
                freq_heart = max_i * 481.88/4096.00;
            }
        }

        printf("\nHeart Rate: %f BPM \n",(freq_heart)*60 );

    }

}



//*****************************************************************************
//
//! Board Initialization & Configuration
//!
//! \param  None
//!
//! \return None
//
//*****************************************************************************
static void
BoardInit(void)
{
/* In case of TI-RTOS vector table is initialize by OS itself */
#ifndef USE_TIRTOS
  //
  // Set vector table base
  //
#if defined(ccs)
    MAP_IntVTableBaseSet((unsigned long)&g_pfnVectors[0]);
#endif
#if defined(ewarm)
    MAP_IntVTableBaseSet((unsigned long)&__vector_table);
#endif
#endif
    //
    // Enable Processor
    //
    MAP_IntMasterEnable();
    MAP_IntEnable(FAULT_SYSTICK);

    PRCMCC3200MCUInit();
}

//*****************************************************************************
//
//! Main function for spi demo application
//!
//! \param none
//!
//! \return None.
//
//*****************************************************************************
void main()
 {
    //
    // Initialize Board configurations
    //
    BoardInit();

    //
    // Muxing UART and SPI lines.
    //
    PinMuxConfig();

    //
    // Enable the SPI module clock
    //
    MAP_PRCMPeripheralClkEnable(PRCM_GSPI,PRCM_RUN_MODE_CLK);

    //
    // Initialising the Terminal.
    //
    InitTerm();

    //
    // Clearing the Terminal.
    //
    ClearTerm();

    //
    // Reset the peripheral
    //
    MAP_PRCMPeripheralReset(PRCM_GSPI);


#if MASTER_MODE

    MasterMain();

#else

    SlaveMain();

#endif

    while(1)
    {

    }

}

